import React from 'react'
import './../styles/Cart.css'
import {connect} from 'react-redux'
import * as actions from './../actions/index'

const mapStateToProps = (state) => {
    return {
        cart: state.products.cart,
    };
}

export default connect(
    mapStateToProps,
    null
)
(function Cart({cart}) {
    let sum = 0;
    cart.forEach(item => {
        sum += item.price;    
    })
    return (
        <div className="item">
            <h1>Giỏ hàng của bạn:</h1>
            {
                cart.map(item => 
                    <div className="item__product" key = {item.id}>
                        <input type="checkbox" autocomplete="off" className="choose__item" value = {true}
                        onChange = {(event) => {
                            if (event.target.value) {
                                sum += item.price;
                            }
                            else sum -= item.price;
                        }}
                        />
                        <img
                            src={item.src}
                            alt={item.name}
                            className="img_item"
                        />
                        <div className="info__item">
                            <h3>{item.name}</h3>
                            <strong className="price__item">{item.price}₫</strong>
                        </div>
                        <input type="number" className="number__item"/>
                        <i className="fa fa-trash delete_item" aria-hidden="true"></i>
                    </div>    
                )
            }
            <br />
            <h4>Tổng giá:</h4>
            <strong className="price__item">
                {sum}₫
            </strong>
            <br />
            <button type="button" name="" id="" className="btn btn-primary">Thanh toán</button>
        </div>
    )
})
