import * as types from "./../constants/ActionType";

export const addCart = (product) => {
  return {
    type: types.ADD_CART,
    cart: product,
  };
};

export const deleteProduct = (product) => {
  return {
    type: types.DELETE_PRODUCT,
    cart: product,
  };
};
