import React from 'react'
import './../styles/Product.css'
import {connect} from 'react-redux'
import * as actions from './../actions/index'

const mapStateToProps = (state) => {
    return {
    isEdit: state.tasks.isEdit,
    editTask: state.tasks.editTask,
    };
};

const mapDispatchToProps = (dispatch, props) => {
return {
    onSubmit: (content) => {
    dispatch(actions.addTask(content));
    },
    setIsEdit: (check) => {
    dispatch(actions.setIsEdit(check));
    },
    setEditTask: (editTask) => {
    dispatch(actions.setEditTask(editTask));
    },
};
};

export default function Products() {
    return (
        <div className="product">
            <div className="product__item">
                <img src="https://cdn.tgdd.vn/Products/Images/42/214909/samsung-galaxy-note-10-lite-chi-tiet-1-400x460.png"
                alt="Iphone 11 Pro Max"
                className="img_product"
                />
                <h4 className="name_product">Samsung Galaxy Note 10 Lite</h4>
                <p className="price_product">11.490.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
            <div className="product__item">
                <img src="https://clickbuy.com.vn/uploads/2019/03/iPhone-7-jetback.png"
                alt="Iphone 7 Lock 32Gb cũ"
                className="img_product"
                />
                <h4 className="name_product">Iphone 7 Lock 32Gb cũ</h4>
                <p className="price_product">3.190.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
            <div className="product__item">
                <img src="https://clickbuy.com.vn/uploads/2019/03/apple-iphone-xr-vang-600x600.png"
                alt="Iphone XR 64Gb quốc tế cũ"
                className="img_product"
                />
                <h4 className="name_product">Iphone XR 64Gb quốc tế cũ</h4>
                <p className="price_product">10.690.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
            <div className="product__item">
                <img src="https://didongthongminh.vn/upload_images/2019/01/SAMSUNG-GALAXY-S10-WHITE.png"
                alt="Samsung Galaxy S10"
                className="img_product"
                />
                <h4 className="name_product">Samsung Galaxy S10</h4>
                <p className="price_product">8.290.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
            <div className="product__item">
                <img src="https://phuongtung.vn/pt/images/detailed/10/oppo-a1k-400x460.png"
                alt="Oppo A1K"
                className="img_product"
                />
                <h4 className="name_product">Oppo A1K</h4>
                <p className="price_product">2.690.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
            <div className="product__item">
                <img src="https://cdn.tgdd.vn/Products/Images/42/216172/oppo-reno3-pro-den-400x460-400x460.png"
                alt="Oppo Reno3 Pro"
                className="img_product"
                />
                <h4 className="name_product">Oppo Reno3 Pro</h4>
                <p className="price_product">13.990.000₫</p>
                <button type="button" name="" id="" className="btn btn-primary">Thêm vào giỏ hàng</button>
            </div>
        </div>
    )
}
