import React, {useState, useEffect} from 'react'
import './../styles/Cart.css'
import {connect} from 'react-redux'
import * as actions from './../actions/index'

const mapStateToProps = (state) => {
    return {
        cart: state.products.cart,
    };
}

export default connect(
    mapStateToProps,
    null
)
(function Cart({cart}) {
    let sum = 0;
    const [cartProduct, setCartProduct] = useState([]);

    useEffect(() => {
        cart.forEach(item => {
            cartProduct.push({
                amount: 1,
                cart: item
            });
        })
        setCartProduct([...cartProduct]);
    }, [cart]);

    cartProduct.forEach(item => {
        sum += item.cart.price * item.amount;    
    })

    return (
        <div className="item">
            <h1>Giỏ hàng của bạn:</h1>
            {   cart ?
                cartProduct.map((item, index) => 
                    <div className="item__product" key = {item.cart.id}>
                        <input type="checkbox" className="choose__item" checked={true} />
                        <img
                            src={item.cart.src}
                            alt={item.cart.name}
                            className="img_item"
                        />
                        <div className="info__item">
                            <h3>{item.cart.name}</h3>
                            <strong className="price__item">{item.cart.price}₫</strong>
                        </div>
                        <div className="number__item">
                            <button className="btn btn-secondary add__item"
                                onClick={() => {
                                    if (cartProduct[index].amount > 0) {
                                        cartProduct[index].amount -= 1;
                                        setCartProduct([...cartProduct]);
                                    }
                                }}
                            >-</button>
                            <div className="amount__item">{item.amount}</div>
                            <button className="btn btn-secondary add__item"
                                onClick={() => {
                                    cartProduct[index].amount += 1;
                                    setCartProduct([...cartProduct]);
                                }}
                            >+</button>
                        </div>
                        <i className="fa fa-trash delete_item" aria-hidden="true"></i>
                    </div>    
                ) :
                'Giỏ hàng của bạn đang trống'
            }
            <br />
            <h4>Tổng giá:</h4>
            <strong className="price__item">
                {sum}₫
            </strong>
            <br />
            <button type="button" name="" id="" className="btn btn-primary">Thanh toán</button>
        </div>
    )
})
